import React, { useState, useEffect } from 'react';
import './WeatherAlerts.css';  // Include the CSS file for styling

const WeatherAlerts = () => {
  const [subscribedTopics, setSubscribedTopics] = useState([]);
  const [alert, setAlert] = useState('');
  const [fireNews, setFireNews] = useState([]);
  const [location, setLocation] = useState('California'); // Default location

  const topics = ['California', 'Texas', 'Florida']; // Available topics (States)

  // Fetch fire news from NewsAPI
  const fetchFireNews = async (location) => {
    const apiKey = '0f6b574a3341404295dde5d943a529da';  // Replace with your NewsAPI key
    const response = await fetch(`https://newsapi.org/v2/everything?q=fire&language=en&apiKey=${apiKey}`);
    const data = await response.json();
    const filteredArticles = data.articles.filter((article) => article.title.includes(location));
    setFireNews(filteredArticles);
  };

  // Fetch wildfire alerts from OpenWeather API
  const fetchFireAlerts = async (lat, lon) => {
    const apiKey = 'YOUR_OPENWEATHER_API_KEY';  // Replace with your OpenWeather key
    const response = await fetch(`https://www.fema.gov/api/open/v2/DisasterDeclarationsSummaries?incidentType=Fire`);
    const data = await response.json();
    const wildfireAlerts = data.alerts ? data.alerts.filter(alert => alert.event === "Wildfire") : [];
    if (wildfireAlerts.length > 0) {
      setAlert(wildfireAlerts[0].description);
    }
  };

  // Fetch FEMA disaster information for fire incidents in a specific state
  const fetchFemaDisasters = async (state) => {
    const response = await fetch(`https://www.fema.gov/api/open/v2/DisasterDeclarationsSummaries?state=${state}&incidentType=Fire`);
    const data = await response.json();
    // You can filter and display fire-related disasters
    const fireIncidents = data.incidents.filter(incident => incident.eventType === 'Fire');
    if (fireIncidents.length > 0) {
      setAlert(fireIncidents[0].incidentType);  // Example: Displaying incident type
    }
  };

  const subscribeToTopic = (topic) => {
    setSubscribedTopics((prev) => [...prev, topic]);
    console.log(`Subscribed to ${topic} alerts`);
    fetchFireNews(topic);
    // Simulate fetching fire alerts based on location
    if (topic === 'California') {
      //fetchFireAlerts(36.7783, -119.4179);  // Example: California Lat/Lon
      fetchFemaDisasters('CA');
    }
  };

  useEffect(() => {
    if (subscribedTopics.length > 0) {
      subscribedTopics.forEach((topic) => {
        fetchFireNews(topic);
        if (topic === 'California') {
         // fetchFireAlerts(36.7783, -119.4179);
          fetchFemaDisasters('CA');
        }
      });
    }
  }, [subscribedTopics]);

  return (
    <div className="weather-alerts">
      <h1>Weather Alerts</h1>
      <div className="subscription">
        <h2>Subscribe to Alerts</h2>
        {topics.map((topic) => (
          <button key={topic} onClick={() => subscribeToTopic(topic)} className="subscribe-btn">
            Subscribe to {topic} Alerts
          </button>
        ))}
      </div>

      <div className="subscribed-topics">
        <h3>Subscribed Topics:</h3>
        <ul>
          {subscribedTopics.map((topic, index) => (
            <li key={index}>{topic}</li>
          ))}
        </ul>
      </div>

      {alert && <div className="alert"><strong>{alert}</strong></div>}

      <div className="fire-news">
        <h3>Fire News</h3>
        {fireNews.length > 0 ? (
          fireNews.map((article, index) => (
            <div key={index} className="news-article">
              <h4>{article.title}</h4>
              <p>{article.description}</p>
              <a href={article.url} target="_blank" rel="noopener noreferrer">Read more</a>
            </div>
          ))
        ) : (
          <p>No news available for this location.</p>
        )}
      </div>
    </div>
  );
};

export default WeatherAlerts;
