import React, { useState, useEffect } from "react";

const WEBSOCKET_URL = "ws://localhost:8081"; // Backend WebSocket server

function App() {
  const [weather, setWeather] = useState(null);
  const [location, setLocation] = useState(null);
  const [error, setError] = useState("");
  const [ws, setWs] = useState(null);

  useEffect(() => {
    // Check geolocation availability
    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const loc = {
            latitude: position.coords.latitude,
            longitude: position.coords.longitude,
          };
          setLocation(loc);
          sendLocationToServer(loc);
        },
        (error) => setError("Unable to fetch location")
      );
    } else {
      setError("Geolocation is not supported by this browser.");
    }

    return () => {
      if (ws) {
        ws.close(); // Clean up WebSocket on unmount
      }
    };
  }, []);

  const sendLocationToServer = (loc) => {
    const ws = new WebSocket(WEBSOCKET_URL);
    setWs(ws); // Store the WebSocket instance

    ws.onopen = () => {
      ws.send(JSON.stringify(loc)); // Send location when connected
    };

    ws.onmessage = (event) => {
      const weatherData = JSON.parse(event.data);
      setWeather(weatherData); // Update weather state with received data
    };

    ws.onerror = (err) => {
      setError("WebSocket error occurred.");
      console.error("WebSocket error:", err);
    };

    ws.onclose = () => {
      setError("WebSocket connection closed.");
    };
  };

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h2>Real-Time Weather Updates</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {location && (
        <p>
          Location: {location.latitude}, {location.longitude}
        </p>
      )}
      {weather ? (
        <div>
          <h3>{weather.city}</h3>
          <p>Temperature: {weather.temp}Â°C</p>
          <p>Windspeed: {weather.windspeed} km/h</p>
        </div>
      ) : (
        <p>Waiting for weather updates...</p>
      )}
    </div>
  );
}

export default App;
