import React, { useState, useEffect } from 'react';
import './WeatherAlerts.css';

const WeatherAlerts = () => {
  const [subscribedTopics, setSubscribedTopics] = useState([]);
  const [alert, setAlert] = useState('');

  const topics = ['LosAngeles', 'SanFrancisco', 'SouthernCalifornia']; // Available topics

  const subscribeToTopic = (topic) => {
    // Simulate subscribing to a topic
    setSubscribedTopics((prev) => [...prev, topic]);
    console.log(`Subscribed to ${topic} alerts`);
  };

  // Simulate receiving an alert for subscribed topics
  useEffect(() => {
    // Here, you would ideally use WebSocket or another method to listen for updates from the Kafka consumer
    const interval = setInterval(() => {
      if (subscribedTopics.length > 0) {
        // Simulate receiving an alert for a subscribed topic
        setAlert(`Weather alert for ${subscribedTopics.join(', ')}: Severe weather approaching!`);
      }
    }, 5000);

    return () => clearInterval(interval);
  }, [subscribedTopics]);

  return (
    <div className="weather-alerts-container">
      <h1>Weather Alerts</h1>
      <p>Subscribe to real-time weather alerts for your location.</p>
      <div>
        <h2>Subscribe to Alerts</h2>
        {topics.map((topic) => (
          <button key={topic} onClick={() => subscribeToTopic(topic)}>
            Subscribe to {topic} Alerts
          </button>
        ))}
      </div>
      <div>
        <h3>Subscribed Topics:</h3>
        <ul>
          {subscribedTopics.map((topic, index) => (
            <li key={index}>{topic}</li>
          ))}
        </ul>
      </div>
      {alert && <div><strong>{alert}</strong></div>}
    </div>
  );
};

export default WeatherAlerts;
