import React, { useState, useEffect } from "react";

// WebSocket URL of the backend server (WebSocket connection)
const WEBSOCKET_URL = "ws://localhost:8081";

function App() {
  const [weather, setWeather] = useState(null);
  const [location, setLocation] = useState(null);
  const [error, setError] = useState("");
  const [subscribedTopics, setSubscribedTopics] = useState([]);  // Tracks user subscriptions

  useEffect(() => {
    // Fetch user's geolocation on app load
    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const loc = {
            latitude: position.coords.latitude,
            longitude: position.coords.longitude,
          };
          setLocation(loc);
          sendLocationToServer(loc);  // Send location to the backend
        },
        (error) => setError("Unable to fetch location")
      );
    } else {
      setError("Geolocation is not supported by this browser.");
    }
  }, []);

  const sendLocationToServer = (loc) => {
    const ws = new WebSocket(WEBSOCKET_URL);
    ws.onopen = () => {
      ws.send(JSON.stringify(loc));  // Send location to backend for alert routing
    };
    ws.onmessage = (event) => {
      const weatherData = JSON.parse(event.data);
      setWeather(weatherData);
    };
    ws.onerror = (err) => setError("WebSocket error occurred.");
  };

  const subscribeToTopic = (location) => {
    // Connect WebSocket again to subscribe to the selected location
    const ws = new WebSocket(WEBSOCKET_URL);
    ws.onopen = () => {
      ws.send(JSON.stringify({ subscribe: location }));  // Send subscription request for the specific location
      setSubscribedTopics([...subscribedTopics, location]);  // Update UI with subscribed topic
    };
  };

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h2>Real-Time Weather Updates</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {location && (
        <p>
          Location: {location.latitude}, {location.longitude}
        </p>
      )}
      {weather ? (
        <div>
          <h3>{weather.city}</h3>
          <p>Temperature: {weather.temp}Â°C</p>
          <p>Windspeed: {weather.windspeed} km/h</p>
        </div>
      ) : (
        <p>Waiting for weather updates...</p>
      )}

      <h3>Subscribe to Topics</h3>
      <button onClick={() => subscribeToTopic("los-angeles")}>Subscribe to Los Angeles Alerts</button>
      <button onClick={() => subscribeToTopic("san-francisco")}>Subscribe to San Francisco Alerts</button>
      <button onClick={() => subscribeToTopic("southern-california")}>Subscribe to Southern California Alerts</button>

      <h4>Subscribed Topics</h4>
      <ul>
        {subscribedTopics.map((topic, index) => (
          <li key={index}>{topic}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
